public int active()
{
    DirPersonUser dirPersonUser;
    HcmWorker HCMWorker = this.cursor();

    int ret = next active();

    dirPersonUser = DirPersonUser::findParty(HcmWorker.Person);

    if(dirPersonUser.RecId)
    {
        this.formRun().control(this.formRun().controlId("AAXInventSiteUserTab")).enabled(true);
    }
    else
    {
        this.formRun().control(this.formRun().controlId("AAXInventSiteUserTab")).enabled(false);
    }


    return ret;

}

public void selectionChanged()
{
    DirPersonUser dirPersonUser;
    HcmWorker HCMWorker = this.cursor();

    next selectionChanged();

    dirPersonUser = DirPersonUser::findParty(HcmWorker.Person);

    if(dirPersonUser.RecId)
    {
        this.formRun().control(this.formRun().controlId("AAXInventSiteUserTab")).enabled(true);
    }
    else
    {
        this.formRun().control(this.formRun().controlId("AAXInventSiteUserTab")).enabled(false);
    }
}
